// Toma la entrada del nodo anterior y limpia el JSON manteniendo solo los campos especificados
// Genera salida en formato markdown estructurado para IA

// Obtener el primer elemento y acceder a data.context
const inputData = $('Webhook').first().json.body[0].body.context;

// Campos que queremos mantener
const fieldsToKeep = [
  'name',
  'id', 
  'code',
  'parent',
  'status',
  'family',
  'cityInstall',
  'productType',
  'countryInstall',
  'addressInstall',
  'bandwidthService',
  'bandwidthServiceUnit',
  'activationDate'
];

// Funci√≥n para verificar si una fecha est√° dentro de los √∫ltimos 20 d√≠as
function isWithinLast20Days(dateString) {
  if (!dateString || dateString.trim() === '') {
    return false;
  }

  // Parseamos la fecha en formato MM/DD/YYYY
  const dateParts = dateString.split('/');
  if (dateParts.length !== 3) {
    return false;
  }

  const month = parseInt(dateParts[0], 10);
  const day = parseInt(dateParts[1], 10);
  const year = parseInt(dateParts[2], 10);

  const itemDate = new Date(year, month - 1, day);
  const today = new Date();
  const twentyDaysAgo = new Date();
  twentyDaysAgo.setDate(today.getDate() - 20);

  return itemDate >= twentyDaysAgo && itemDate <= today;
}

// Funci√≥n para limpiar un objeto manteniendo solo los campos especificados
function cleanObject(obj) {
  const cleanedObj = {};

  fieldsToKeep.forEach(field => {
    if (obj.hasOwnProperty(field)) {
      cleanedObj[field] = obj[field];
    } else {
      cleanedObj[field] = ""; // Valor por defecto si el campo no existe
    }
  });

  return cleanedObj;
}

// Funci√≥n para generar markdown estructurado
function generateMarkdown(services) {
  let markdown = "# Listado de Servicios\n\n";

  // Agrupar por status
  const groupedByStatus = {};
  services.forEach(service => {
    const status = service.status || 'Sin Estado';
    if (!groupedByStatus[status]) {
      groupedByStatus[status] = [];
    }
    groupedByStatus[status].push(service);
  });

  // Ordenar los status (Activo, Vendido, Suspendido, Cancelado)
  const statusOrder = ['Activo', 'Vendido', 'Suspendido', 'Cancelado', 'Sin Estado'];

  statusOrder.forEach(status => {
    if (groupedByStatus[status] && groupedByStatus[status].length > 0) {
      markdown += `## Servicios con Estado: ${status}\n\n`;
      markdown += `Total de servicios: ${groupedByStatus[status].length}\n\n`;

      // Agrupar por familia de producto
      const groupedByFamily = {};
      groupedByStatus[status].forEach(service => {
        const family = service.family || 'Sin Familia';
        if (!groupedByFamily[family]) {
          groupedByFamily[family] = [];
        }
        groupedByFamily[family].push(service);
      });

      // Mostrar cada familia
      Object.keys(groupedByFamily).sort().forEach(family => {
        markdown += `### Familia: ${family}\n\n`;

        // Tabla de servicios
        markdown += "| Nombre | ID | C√≥digo | Tipo de Producto | Pa√≠s | Ciudad | Ancho de Banda | Fecha Activaci√≥n |\n";
        markdown += "|--------|----|---------|-----------------|---------|---------|-----------------|-----------------|\n";

        groupedByFamily[family].forEach(service => {
          const bandwidth = service.bandwidthService + (service.bandwidthServiceUnit ? ` ${service.bandwidthServiceUnit}` : '');
          markdown += `| ${service.name || 'N/A'} | ${service.id || 'N/A'} | ${service.code || 'N/A'} | ${service.productType || 'N/A'} | ${service.countryInstall || 'N/A'} | ${service.cityInstall || 'N/A'} | ${bandwidth || 'N/A'} | ${service.activationDate || 'N/A'} |\n`;
        });

        markdown += "\n";
      });
    }
  });

  // Resumen general
  markdown += "## Resumen General\n\n";
  markdown += `Total de servicios procesados: ${services.length}\n\n`;

  // Conteo por status
  markdown += "### Distribuci√≥n por Estado\n\n";
  statusOrder.forEach(status => {
    if (groupedByStatus[status] && groupedByStatus[status].length > 0) {
      markdown += `- ${status}: ${groupedByStatus[status].length} servicios\n`;
    }
  });

  // Conteo por familia
  markdown += "\n### Distribuci√≥n por Familia de Producto\n\n";
  const allFamilies = {};
  services.forEach(service => {
    const family = service.family || 'Sin Familia';
    allFamilies[family] = (allFamilies[family] || 0) + 1;
  });

  Object.keys(allFamilies).sort().forEach(family => {
    markdown += `- ${family}: ${allFamilies[family]} servicios\n`;
  });

  return markdown;
}

// Array para almacenar todos los elementos limpios
const processedServices = [];
// Set para trackear IDs ya procesados y evitar duplicados
const processedIds = new Set();

// Verificar si inputData es un array y procesarlo
if (Array.isArray(inputData)) {
  // Limpiar cada objeto en el array context y agregarlo como elemento separado
  inputData.forEach(contextItem => {
    // Verificar si el ID ya fue procesado para evitar duplicados
    if (processedIds.has(contextItem.id)) {
      return; // Saltar si ya existe
    }

    // Si el status es "Cancelado", verificar si est√° dentro de los √∫ltimos 20 d√≠as
    if (contextItem.status === "Cancelado") {
      if (isWithinLast20Days(contextItem.activationDate)) {
        const cleanedItem = cleanObject(contextItem);
        processedServices.push(cleanedItem);
        processedIds.add(contextItem.id); // Marcar como procesado
      }
    } else {
      // Si no es "Cancelado", incluirlo normalmente
      const cleanedItem = cleanObject(contextItem);
      processedServices.push(cleanedItem);
      processedIds.add(contextItem.id); // Marcar como procesado
    }
  });
}

// Generar el markdown
const markdownOutput = generateMarkdown(processedServices);

// Retornar como un solo elemento con el markdown
return [{
  json: {
    markdown: markdownOutput,
    message:$('Webhook').first().json.body[0].body.message,
    tipo:$('Webhook').first().json.body[0].body.context[1],
    totalServices: processedServices.length,
    duplicatesRemoved: inputData.length - processedServices.length,
    generatedAt: new Date().toISOString()
  }
}];

// C√≥digo JavaScript para nodo n8n - Organizaci√≥n de datos de casos de soporte
// Procesa datos fraccionados y los estructura con campos clave limpios

// Funci√≥n principal para procesar los datos
function procesarCasosSoporte() {
    // Obtener los datos de entrada del nodo n8n
    const inputData = $input.all();

    // Array para almacenar los casos organizados
    let casosOrganizados = [];

    // Procesar cada elemento de entrada
    for (let i = 0; i < inputData.length; i++) {
        const item = inputData[i];

        // Verificar si hay contexto con casos
        if (item.json.body && item.json.body.context) {
            const casos = item.json.body.context;

            // Procesar cada caso en el contexto
            casos.forEach(caso => {
                if (caso.info && caso.contact) {
                    // Extraer y limpiar los campos seg√∫n la estructura especificada
                    const casoLimpio = {
                        info: {
                            title: caso.info.title || "",
                            casenumber: caso.info.casenumber || "",
                            assigned: {
                                id: caso.info.assigned?.id || "",
                                value: caso.info.assigned?.value || ""
                            },
                            startdate: caso.info.startdate || "",
                            enddate: caso.info.enddate || "",
                            phonealt: caso.info.phonealt || "",
                            category: {
                                id: caso.info.category?.id || "",
                                value: caso.info.category?.value || ""
                            },
                            services: {
                                id: caso.info.services?.id || "",
                                value: caso.info.services?.value || ""
                            },
                            status: {
                                id: caso.info.status?.id || "",
                                value: caso.info.status?.value || ""
                            }
                        },
                        contact: {
                            country: {
                                id: caso.contact.country?.id || "",
                                value: caso.contact.country?.value || ""
                            },
                            city: {
                                id: caso.contact.city?.id || "",
                                value: caso.contact.city?.value || ""
                            },
                            email: caso.contact.email || ""
                        }
                    };

                    casosOrganizados.push(casoLimpio);
                }
            });
        }
    }

    return casosOrganizados;
}

// Funci√≥n para buscar un caso espec√≠fico por n√∫mero
function buscarCasoPorNumero(casos, numeroCaso) {
    return casos.find(caso => caso.info.casenumber === numeroCaso);
}

// Funci√≥n para filtrar casos por estado
function filtrarCasosPorEstado(casos, estado) {
    return casos.filter(caso => caso.info.status.value.toLowerCase() === estado.toLowerCase());
}

// Funci√≥n para agrupar casos por servicio
function agruparCasosPorServicio(casos) {
    const agrupados = {};

    casos.forEach(caso => {
        const servicio = caso.info.services.value;
        if (!agrupados[servicio]) {
            agrupados[servicio] = [];
        }
        agrupados[servicio].push(caso);
    });

    return agrupados;
}

// Funci√≥n para obtener estad√≠sticas de casos
function obtenerEstadisticas(casos) {
    const estadisticas = {
        total: casos.length,
        abiertos: 0,
        cerrados: 0,
        en_proceso: 0,
        servicios_unicos: new Set(),
        grupos_asignados: new Set()
    };

    casos.forEach(caso => {
        // Contar por estado
        const estado = caso.info.status.value.toLowerCase();
        if (estado === 'abierto') {
            estadisticas.abiertos++;
        } else if (estado === 'cerrado') {
            estadisticas.cerrados++;
        } else if (estado === 'en proceso') {
            estadisticas.en_proceso++;
        }

        // Recopilar servicios √∫nicos
        estadisticas.servicios_unicos.add(caso.info.services.value);

        // Recopilar grupos asignados √∫nicos
        estadisticas.grupos_asignados.add(caso.info.assigned.value);
    });

    // Convertir Sets a arrays
    estadisticas.servicios_unicos = Array.from(estadisticas.servicios_unicos);
    estadisticas.grupos_asignados = Array.from(estadisticas.grupos_asignados);

    return estadisticas;
}

// EJECUCI√ìN PRINCIPAL DEL NODO N8N
try {
    // Procesar todos los casos
    const casosLimpios = procesarCasosSoporte();

    // Obtener el mensaje de consulta si existe
    const inputData = $input.all();
    let mensajeConsulta = "";
    if (inputData[0]?.json?.body?.message) {
        mensajeConsulta = inputData[0].json.body.message;
    }

    // Buscar caso espec√≠fico si se menciona un n√∫mero en la consulta
    let casoEspecifico = null;
    const numeroMatch = mensajeConsulta.match(/TT\d+/);
    if (numeroMatch) {
        const numeroCaso = numeroMatch[0];
        casoEspecifico = buscarCasoPorNumero(casosLimpios, numeroCaso);
    }

    // Obtener estad√≠sticas
    const estadisticas = obtenerEstadisticas(casosLimpios);

    // Agrupar casos por estado
    const casosPorEstado = {
        abiertos: filtrarCasosPorEstado(casosLimpios, 'abierto'),
        cerrados: filtrarCasosPorEstado(casosLimpios, 'cerrado'),
        en_proceso: filtrarCasosPorEstado(casosLimpios, 'en proceso')
    };

    // Generar respuesta en formato Markdown
    let markdown = "# üìã Informe de Casos de Soporte T√©cnico\n\n";

    // Informaci√≥n de la consulta
    if (mensajeConsulta) {
        markdown += `**Consulta original:** ${mensajeConsulta}\n\n`;
    }

    markdown += `**Total de casos procesados:** ${casosLimpios.length}\n`;
    markdown += `**Fecha del informe:** ${new Date().toLocaleString('es-CO')}\n\n`;

    // Caso espec√≠fico solicitado
    if (casoEspecifico) {
        markdown += "## üéØ Caso Solicitado\n\n";
        markdown += `### ${casoEspecifico.info.title}\n\n`;
        markdown += `| Campo | Valor |\n`;
        markdown += `|-------|-------|\n`;
        markdown += `| **N√∫mero de Caso** | \`${casoEspecifico.info.casenumber}\` |\n`;
        markdown += `| **Estado** | ${casoEspecifico.info.status.value} |\n`;
        markdown += `| **Fecha Inicio** | ${casoEspecifico.info.startdate} |\n`;
        markdown += `| **Fecha Fin** | ${casoEspecifico.info.enddate || 'En curso'} |\n`;
        markdown += `| **Categor√≠a** | ${casoEspecifico.info.category.value} |\n`;
        markdown += `| **Servicio** | ${casoEspecifico.info.services.value} |\n`;
        markdown += `| **Grupo Asignado** | ${casoEspecifico.info.assigned.value} |\n`;
        markdown += `| **Pa√≠s** | ${casoEspecifico.contact.country.value} |\n`;
        markdown += `| **Ciudad** | ${casoEspecifico.contact.city.value} |\n`;
        markdown += `| **Email** | ${casoEspecifico.contact.email} |\n\n`;
    }

    // Estad√≠sticas generales
    markdown += "## üìä Estad√≠sticas Generales\n\n";
    markdown += `- **Total de casos:** ${estadisticas.total}\n`;
    markdown += `- **Casos abiertos:** ${estadisticas.abiertos} (${Math.round(estadisticas.abiertos/estadisticas.total*100)}%)\n`;
    markdown += `- **Casos cerrados:** ${estadisticas.cerrados} (${Math.round(estadisticas.cerrados/estadisticas.total*100)}%)\n`;
    markdown += `- **Casos en proceso:** ${estadisticas.en_proceso} (${Math.round(estadisticas.en_proceso/estadisticas.total*100)}%)\n\n`;

    // Casos por estado
    markdown += "## üìã Casos por Estado\n\n";

    if (casosPorEstado.abiertos.length > 0) {
        markdown += `### üîì Casos Abiertos (${casosPorEstado.abiertos.length})\n\n`;
        casosPorEstado.abiertos.slice(0, 10).forEach((caso, index) => {
            markdown += `${index + 1}. **${caso.info.casenumber}** - ${caso.info.title}\n`;
            markdown += `   - üìÖ Fecha: ${caso.info.startdate}\n`;
            markdown += `   - üè¢ Servicio: ${caso.info.services.value}\n`;
            markdown += `   - üë• Asignado: ${caso.info.assigned.value}\n\n`;
        });
        if (casosPorEstado.abiertos.length > 10) {
            markdown += `*... y ${casosPorEstado.abiertos.length - 10} casos m√°s*\n\n`;
        }
    }

    if (casosPorEstado.cerrados.length > 0) {
        markdown += `### ‚úÖ Casos Cerrados (${casosPorEstado.cerrados.length})\n\n`;
        casosPorEstado.cerrados.slice(0, 5).forEach((caso, index) => {
            markdown += `${index + 1}. **${caso.info.casenumber}** - ${caso.info.title}\n`;
            markdown += `   - üìÖ Cerrado: ${caso.info.enddate}\n`;
            markdown += `   - üè¢ Servicio: ${caso.info.services.value}\n\n`;
        });
        if (casosPorEstado.cerrados.length > 5) {
            markdown += `*... y ${casosPorEstado.cerrados.length - 5} casos m√°s*\n\n`;
        }
    }

    if (casosPorEstado.en_proceso.length > 0) {
        markdown += `### ‚è≥ Casos en Proceso (${casosPorEstado.en_proceso.length})\n\n`;
        casosPorEstado.en_proceso.forEach((caso, index) => {
            markdown += `${index + 1}. **${caso.info.casenumber}** - ${caso.info.title}\n`;
            markdown += `   - üìÖ Fecha: ${caso.info.startdate}\n`;
            markdown += `   - üè¢ Servicio: ${caso.info.services.value}\n`;
            markdown += `   - üë• Asignado: ${caso.info.assigned.value}\n\n`;
        });
    }

    // Servicios m√°s afectados
    markdown += "## üè¢ Servicios M√°s Afectados\n\n";
    const serviciosAgrupados = agruparCasosPorServicio(casosLimpios);
    const serviciosOrdenados = Object.entries(serviciosAgrupados)
        .sort((a, b) => b[1].length - a[1].length)
        .slice(0, 5);

    serviciosOrdenados.forEach((servicio, index) => {
        markdown += `${index + 1}. **${servicio[0]}** - ${servicio[1].length} casos\n`;
    });
    markdown += "\n";

    // Grupos de soporte
    markdown += "## üë• Distribuci√≥n por Grupos de Soporte\n\n";
    const gruposConteo = {};
    casosLimpios.forEach(caso => {
        const grupo = caso.info.assigned.value;
        gruposConteo[grupo] = (gruposConteo[grupo] || 0) + 1;
    });

    Object.entries(gruposConteo)
        .sort((a, b) => b[1] - a[1])
        .forEach((grupo, index) => {
            markdown += `${index + 1}. **${grupo[0]}** - ${grupo[1]} casos\n`;
        });

    markdown += "\n---\n\n";
    markdown += "*Informe generado autom√°ticamente por el sistema de soporte t√©cnico*";

    // Retornar datos para n8n con markdown
    return [{
        json: {
            markdown: markdown,
            caso_solicitado: casoEspecifico,
            estadisticas: estadisticas,
            total_casos: casosLimpios.length
        }
    }];

} catch (error) {
    // Manejo de errores para n8n
    return [{
        json: {
            error: true,
            mensaje: "Error al procesar los casos: " + error.message,
            casos_procesados: 0
        }
    }];
}

