// Código para nodo Function en n8n - Análisis de Sentimientos
const inputData = $input.first().json.data;
const fieldsToKeep = ['created_at','customer','caseid','sentiment','strength','confidence','analisis'];

function cleanObject(obj) {
  const cleaned = {};
  fieldsToKeep.forEach(field => {
    cleaned[field] = obj[field] || null;
  });
  return cleaned;
}

function formatDate(dateString) {
  if (!dateString) return 'N/A';
  try {
    const date = new Date(dateString);
    return date.toISOString().split('T')[0];
  } catch {
    return 'N/A';
  }
}

function generateCompactMarkdown(data) {
  const grouped = {POSITIVE: [],NEGATIVE: [],NEUTRAL: []};

  data.forEach(item => {
    const sentiment = item.sentiment?.toUpperCase() || 'NEUTRAL';
    if (grouped[sentiment]) {
      grouped[sentiment].push(item);
    } else {
      grouped.NEUTRAL.push(item);
    }
  });

  let md = `Total:${data.length} Positivo:${grouped.POSITIVE.length} Negativo:${grouped.NEGATIVE.length} Neutral:${grouped.NEUTRAL.length}\n\n`;

  ['POSITIVE', 'NEGATIVE', 'NEUTRAL'].forEach(sentiment => {
    const items = grouped[sentiment];
    if (items.length === 0) return;

    md += `${sentiment} (${items.length}):\n`;

    items.forEach(item => {
      const caseId = (item.caseid || 'N/A').substring(0, 10);
      const customer = (item.customer || 'N/A').substring(0, 50);
      const sentiment = item.sentiment || 'N/A';
      const confidence = parseFloat(item.confidence || 0).toFixed(2);
      const strength = parseFloat(item.strength || 0).toFixed(2);
      const fecha = formatDate(item.created_at);

      md += `caseid:${caseId}, customer:${customer}, sentiment:${sentiment}, confidence:${confidence}, strength:${strength}, date:${fecha}\n`;
    });
    md += '\n';
  });

  const withAnalysis = data.filter(item => item.analisis && item.analisis.trim() !== '');
  if (withAnalysis.length > 0) {
    md += 'Analisis:\n';
    withAnalysis.slice(0, 2).forEach((item, index) => {
      md += `${index + 1}. caseid:${item.caseid}, analisis:${item.analisis.substring(0, 80)}...\n`;
    });
  }

  return md;
}

let processedData = [];
const processedIds = new Set();
let debugInfo = {totalInput: 0, processed: 0, duplicates: 0, errors: 0};

if (Array.isArray(inputData)) {
  debugInfo.totalInput = inputData.length;

  inputData.forEach((item, index) => {
    try {
      const uniqueId = `${item.caseid || 'unknown'}_${item.created_at || index}`;

      if (!processedIds.has(uniqueId)) {
        const cleaned = cleanObject(item);
        processedData.push(cleaned);
        processedIds.add(uniqueId);
        debugInfo.processed++;
      } else {
        debugInfo.duplicates++;
      }
    } catch (error) {
      debugInfo.errors++;
    }
  });
} else {
  debugInfo.errors = 1;
}

const markdownOutput = generateCompactMarkdown(processedData);

const stats = {
  total: processedData.length,
  positive: processedData.filter(item => item.sentiment?.toUpperCase() === 'POSITIVE').length,
  negative: processedData.filter(item => item.sentiment?.toUpperCase() === 'NEGATIVE').length,
  neutral: processedData.filter(item => item.sentiment?.toUpperCase() === 'NEUTRAL').length,
  avgConfidence: processedData.length > 0 ? 
    (processedData.reduce((sum, item) => sum + parseFloat(item.confidence || 0), 0) / processedData.length).toFixed(2) : 0,
  avgStrength: processedData.length > 0 ? 
    (processedData.reduce((sum, item) => sum + parseFloat(item.strength || 0), 0) / processedData.length).toFixed(2) : 0
};

return [{
  json: {
    markdown: markdownOutput,
    stats: stats,
    debug: debugInfo,
    sessionid: $('Webhook').first().json.headers["x-real-ip"],
    consulta: $('Webhook').first().json.body.contents[0].parts[0].text ,
    processedAt: new Date().toISOString().split('T')[0]
  }
}];

