// Obtener los datos del nodo anterior
const items = $input.all();

// Inicialización
let conteo = items.length;

let negativos = [];
let positivos = [];
let sumas = {
  Neutral: { suma: 0, count: 0 },
  Negative: { suma: 0, count: 0 },
  Positive: { suma: 0, count: 0 },
};

// Recorrer cada ítem
for (const item of items) {
  const data = item.json;
  const sentiment = data.sentiment;
  const strength = parseFloat(data.strength);

  // Agrupar por tipo de sentimiento
  if (sumas[sentiment]) {
    sumas[sentiment].suma += strength;
    sumas[sentiment].count += 1;
  }

  // Guardar negativos y positivos
  if (sentiment === 'Negative') {
    negativos.push(data);
  }
  if (sentiment === 'Positive') {
    positivos.push(data);
  }
}

// Encontrar el negativo más fuerte
let peorCasoNegativo = null;
if (negativos.length > 0) {
  peorCasoNegativo = negativos.reduce((prev, curr) =>
    parseFloat(curr.strength) > parseFloat(prev.strength) ? curr : prev
  );
}

// Encontrar el positivo más fuerte
let mejorCasoPositivo = null;
if (positivos.length > 0) {
  mejorCasoPositivo = positivos.reduce((prev, curr) =>
    parseFloat(curr.strength) > parseFloat(prev.strength) ? curr : prev
  );
}

// Calcular promedios y conteos
const promedios = {};
const conteos = {};
for (const tipo in sumas) {
  const entry = sumas[tipo];
  conteos[tipo] = entry.count;
  promedios[tipo] = entry.count > 0 ? entry.suma / entry.count : 0;
}

// Resultado final
return [
  {
    json:{result: {
      cliente:$('Tk_Semana').first().json.customer,
      cantidad_total: conteo,
      conteo_por_sentimiento: conteos,
      promedio_strength: promedios,
      peor_caso_negativo: peorCasoNegativo ? {
        caseid: peorCasoNegativo.caseid,
        strength: peorCasoNegativo.strength,
        confidence: peorCasoNegativo.confidence,
        created_at: peorCasoNegativo.created_at,
        sentiment: peorCasoNegativo.sentiment,
        customer: peorCasoNegativo.customer,
      } : null,
      mejor_caso_positivo: mejorCasoPositivo ? {
        caseid: mejorCasoPositivo.caseid,
        strength: mejorCasoPositivo.strength,
        confidence: mejorCasoPositivo.confidence,
        created_at: mejorCasoPositivo.created_at,
        sentiment: mejorCasoPositivo.sentiment,
        customer: mejorCasoPositivo.customer,
      } : null
    },
  },
  },
];

// Obtener los datos del JSON de entrada
const data = $input.first().json;
const result = data.result;

//Cliente
function cliente(){
  return $input.first().json.result.cliente;
}

// Función para formatear fechas
function formatDate(dateString) {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit'
    });
}

// Función para generar las tarjetas de estadísticas
function generateStatCards(result) {
    const total = result.cantidad_total || 0;
    const positive = result.conteo_por_sentimiento?.Positive || 0;
    const neutral = result.conteo_por_sentimiento?.Neutral || 0;
    const negative = result.conteo_por_sentimiento?.Negative || 0;

    return `
        <div class="stats-grid">
            <div class="stat-card">
                <div class="stat-number">${total}</div>
                <div class="stat-label">Total Casos</div>
            </div>
            <div class="stat-card">
                <div class="stat-number">${positive}</div>
                <div class="stat-label">Positivo</div>
            </div>
            <div class="stat-card">
                <div class="stat-number">${neutral}</div>
                <div class="stat-label">Neutral</div>
            </div>
            <div class="stat-card">
                <div class="stat-number">${negative}</div>
                <div class="stat-label">Negativo</div>
            </div>
        </div>
    `;
}

// Función para generar la distribución de sentimientos
function generateSentimentBreakdown(result) {
    const conteo = result.conteo_por_sentimiento || {};
    const promedio = result.promedio_strength || {};

    const sentiments = [
        { 
            type: 'positive', 
            label: 'Positivo', 
            count: conteo.Positive || 0, 
            avg: promedio.Positive || 0 
        },
        { 
            type: 'neutral', 
            label: 'Neutral', 
            count: conteo.Neutral || 0, 
            avg: promedio.Neutral || 0 
        },
        { 
            type: 'negative', 
            label: 'Negativo', 
            count: conteo.Negative || 0, 
            avg: promedio.Negative || 0 
        }
    ];

    return sentiments.map(sentiment => `
        <div class="sentiment-item ${sentiment.type}">
            <div class="sentiment-label">
                <span class="badge ${sentiment.type}">${sentiment.label}</span>
            </div>
            <div class="sentiment-values">
                <span><strong>Cantidad:</strong> ${sentiment.count}</span>
                <span><strong>Promedio:</strong> ${sentiment.avg}</span>
            </div>
        </div>
    `).join('');
}

// Función para generar el caso destacado
function generateHighlightCase(result) {
    const mejorCaso = result.mejor_caso_positivo;
    const peorCaso = result.peor_caso_negativo;

    let highlightContent = '';

    // Caso Positivo
    if (mejorCaso) {
        highlightContent += `
            <div class="highlight-case positive-case">
                <h3>🏆 Mejor Caso Positivo</h3>
                <div class="case-details">
                    <div class="case-row">
                        <span class="case-label">ID del Caso:</span>
                        <span class="case-value">${mejorCaso.caseid || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Cliente:</span>
                        <span class="case-value">${mejorCaso.customer || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Sentimiento:</span>
                        <span class="case-value">${mejorCaso.sentiment || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Intensidad:</span>
                        <span class="case-value">${mejorCaso.strength || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Confianza:</span>
                        <span class="case-value">${mejorCaso.confidence || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Fecha:</span>
                        <span class="case-value">${formatDate(mejorCaso.created_at)}</span>
                    </div>
                </div>
            </div>
        `;
    }

    // Caso Negativo
    if (peorCaso) {
        highlightContent += `
            <div class="highlight-case negative-case">
                <h3>⚠️ Peor Caso Negativo</h3>
                <div class="case-details">
                    <div class="case-row">
                        <span class="case-label">ID del Caso:</span>
                        <span class="case-value">${peorCaso.caseid || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Cliente:</span>
                        <span class="case-value">${peorCaso.customer || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Sentimiento:</span>
                        <span class="case-value">${peorCaso.sentiment || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Intensidad:</span>
                        <span class="case-value">${peorCaso.strength || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Confianza:</span>
                        <span class="case-value">${peorCaso.confidence || 'N/A'}</span>
                    </div>
                    <div class="case-row">
                        <span class="case-label">Fecha:</span>
                        <span class="case-value">${formatDate(peorCaso.created_at)}</span>
                    </div>
                </div>
            </div>
        `;
    }

    return highlightContent;
}

// Función para generar mensaje de casos vacíos
function generateEmptyMessage(result) {
    const hasNegative = result.conteo_por_sentimiento?.Negative > 0;
    const hasPositive = result.conteo_por_sentimiento?.Positive > 0;

    let message = '';

    if (!hasNegative && !hasPositive) {
        message = '<div class="no-data"><p>📊 Solo se encontraron casos neutrales en este análisis</p></div>';
    } else if (!hasNegative) {
        message = '<div class="no-data"><p>💡 No se encontraron casos negativos en este análisis</p></div>';
    } else if (!hasPositive) {
        message = '<div class="no-data"><p>⚠️ No se encontraron casos positivos en este análisis</p></div>';
    }

    return message;
}

// Generar el HTML completo
const htmlContent = `
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Reporte de Análisis de Sentimientos</title>
    <style>
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            margin: 0;
            padding: 20px;
            background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
            min-height: 100vh;
        }

        .email-container {
            max-width: 600px;
            margin: 0 auto;
            background: white;
            border-radius: 15px;
            overflow: hidden;
            box-shadow: 0 20px 40px rgba(0,0,0,0.1);
            border: 1px solid #e9ecef;
        }

        .header {
            background: linear-gradient(135deg, #e91e63 0%, #c2185b 100%);
            padding: 30px;
            text-align: center;
            color: white;
        }

        .header h1 {
            margin: 0;
            font-size: 24px;
            font-weight: 600;
        }

        .header p {
            margin: 10px 0 0 0;
            opacity: 0.9;
            font-size: 14px;
        }

        .content {
            padding: 30px;
        }

        .stats-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
            gap: 15px;
            margin-bottom: 30px;
        }

        .stat-card {
            background: linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%);
            padding: 20px;
            border-radius: 12px;
            text-align: center;
            border: 2px solid #e9ecef;
            box-shadow: 0 4px 6px rgba(0,0,0,0.05);
        }

        .stat-number {
            font-size: 28px;
            font-weight: bold;
            color: #e91e63;
            margin-bottom: 5px;
        }

        .stat-label {
            font-size: 14px;
            color: #6c757d;
            text-transform: uppercase;
            letter-spacing: 0.5px;
            font-weight: 600;
        }

        .sentiment-breakdown {
            margin-bottom: 30px;
        }

        .section-title {
            font-size: 18px;
            font-weight: 600;
            color: #495057;
            margin-bottom: 20px;
            border-bottom: 2px solid #e91e63;
            padding-bottom: 10px;
        }

        .sentiment-item {
            display: flex;
            align-items: center;
            justify-content: space-between;
            padding: 15px;
            margin-bottom: 10px;
            border-radius: 8px;
            background: #f8f9fa;
            border-left: 4px solid #dee2e6;
            border: 1px solid #e9ecef;
        }

        .sentiment-item.positive {
            border-left-color: #28a745;
            background: linear-gradient(135deg, #d4edda 0%, #c3e6cb 100%);
        }

        .sentiment-item.neutral {
            border-left-color: #6c757d;
            background: linear-gradient(135deg, #e2e3e5 0%, #d6d8db 100%);
        }

        .sentiment-item.negative {
            border-left-color: #e91e63;
            background: linear-gradient(135deg, #f8d7da 0%, #f5c6cb 100%);
        }

        .sentiment-label {
            font-weight: 600;
            color: #495057;
        }

        .sentiment-values {
            display: flex;
            gap: 15px;
            font-size: 14px;
        }

        .badge {
            padding: 4px 8px;
            border-radius: 20px;
            font-size: 12px;
            font-weight: bold;
            color: white;
        }

        .badge.positive { background: #28a745; }
        .badge.neutral { background: #6c757d; }
        .badge.negative { background: #e91e63; }

        .highlight-case {
            background: linear-gradient(135deg, #007bff 0%, #0056b3 100%);
            color: white;
            padding: 25px;
            border-radius: 12px;
            margin-bottom: 20px;
            border: 2px solid #0056b3;
        }

        .highlight-case h3 {
            margin: 0 0 15px 0;
            font-size: 16px;
            opacity: 0.9;
        }

        .case-details {
            background: rgba(255,255,255,0.1);
            padding: 15px;
            border-radius: 8px;
            backdrop-filter: blur(10px);
        }

        .case-row {
            display: flex;
            justify-content: space-between;
            margin-bottom: 8px;
            font-size: 14px;
        }

        .case-row:last-child {
            margin-bottom: 0;
        }

        .case-label {
            font-weight: 600;
            opacity: 0.9;
        }

        .case-value {
            font-weight: 500;
        }

        .footer {
            background: #495057;
            color: white;
            padding: 20px;
            text-align: center;
            font-size: 12px;
            opacity: 0.9;
        }

        .no-data {
            text-align: center;
            color: #6c757d;
            font-style: italic;
            padding: 20px;
            background: #f8f9fa;
            border-radius: 8px;
            border: 1px solid #e9ecef;
        }

        @media (max-width: 600px) {
            .stats-grid {
                grid-template-columns: repeat(2, 1fr);
            }

            .sentiment-values {
                flex-direction: column;
                gap: 5px;
            }

            .case-row {
                flex-direction: column;
                gap: 2px;
            }
        }
    </style>
</head>
<body>
    <div class="email-container">
        <div class="header">
            <h1>📊 Resumen Análisis de Sentimientos</h1>
            <p>Cliente: ${cliente()}  </p>
        </div>

        <div class="content">
            ${generateStatCards(result)}

            <div class="sentiment-breakdown">
                <h2 class="section-title">Distribución por Sentimiento</h2>
                ${generateSentimentBreakdown(result)}
            </div>

            ${generateHighlightCase(result)}

            ${generateEmptyMessage(result)}
        </div>

        <div class="footer">
            <p>Reporte generado automáticamente • © 2025 Sistema de Análisis de Sentimientos</p>
        </div>
    </div>
</body>
</html>
`;

// Retornar el resultado para n8n
return {
    json: {
        html: htmlContent,
        subject: `Reporte de Sentimientos - ${result.cantidad_total} casos analizados`,
        summary: {
            total: result.cantidad_total,
            positive: result.conteo_por_sentimiento?.Positive || 0,
            neutral: result.conteo_por_sentimiento?.Neutral || 0,
            negative: result.conteo_por_sentimiento?.Negative || 0,
            best_case: result.mejor_caso_positivo?.caseid || null,
            worst_case: result.peor_caso_negativo?.caseid || null
        }
    }
};

parameters.query:
SELECT customer, COUNT(*) AS total_casos
FROM public.sentiment_results
GROUP BY customer;
